import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const CategoriesContainer = styled.section`
  padding: 80px 0;
  background: white;
`;

const CategoriesContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const SectionTitle = styled.h2`
  font-family: ${props => props.theme.fonts.heading};
  font-size: 2.5rem;
  text-align: center;
  color: ${props => props.theme.colors.primary};
  margin-bottom: 3rem;
`;

const CategoriesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 2rem;
  max-width: 1000px;
  margin: 0 auto;
`;

const CategoryCard = styled(motion.div)`
  background: white;
  border-radius: 15px;
  padding: 2rem 1.5rem;
  text-align: center;
  box-shadow: ${props => props.theme.shadows.light};
  cursor: pointer;
  transition: all 0.3s ease;
  border: 2px solid transparent;

  &:hover {
    transform: translateY(-10px);
    box-shadow: ${props => props.theme.shadows.heavy};
    border-color: ${props => props.theme.colors.primary};
  }

  .icon {
    font-size: 3rem;
    margin-bottom: 1rem;
    display: block;
  }

  h3 {
    font-size: 1.2rem;
    color: ${props => props.theme.colors.text};
    margin-bottom: 0.5rem;
  }

  p {
    color: ${props => props.theme.colors.gray};
    font-size: 0.9rem;
  }
`;

const Categories = () => {
  const navigate = useNavigate();

  const categories = [
    {
      icon: 'ðŸ›ï¸',
      title: 'Venues',
      description: 'Beautiful venues for your ceremony',
      category: 'venues',
      url: '/vendors/all/wedding-venues'
    },
    {
      icon: 'ðŸ“¸',
      title: 'Photography',
      description: 'Capture your precious moments',
      category: 'photography',
      url: '/vendors/all/wedding-photographers'
    },
    {
      icon: 'ðŸŒ¸',
      title: 'Decorators',
      description: 'Transform your venue beautifully',
      category: 'decorators',
      url: '/vendors/all/wedding-decorators'
    },
    {
      icon: 'ðŸ°',
      title: 'Catering',
      description: 'Delicious food for your guests',
      category: 'catering',
      url: '/vendors/all/wedding-catering'
    },
    {
      icon: 'ðŸ’„',
      title: 'Makeup Artists',
      description: 'Look stunning on your big day',
      category: 'makeup',
      url: '/vendors?category=makeup'
    },
    {
      icon: 'ðŸŽµ',
      title: 'Entertainment',
      description: 'Music and entertainment services',
      category: 'entertainment',
      url: '/vendors?category=entertainment'
    },
    {
      icon: 'ðŸš—',
      title: 'Transportation',
      description: 'Stylish rides for the couple',
      category: 'transportation',
      url: '/vendors?category=transportation'
    },
    {
      icon: 'ðŸ“‹',
      title: 'Planners',
      description: 'Professional wedding planners',
      category: 'planners',
      url: '/vendors?category=planners'
    }
  ];

  const handleCategoryClick = (category) => {
    navigate(category.url);
  };

  return (
    <CategoriesContainer>
      <CategoriesContent>
        <SectionTitle>Wedding Categories</SectionTitle>
        <CategoriesGrid>
          {categories.map((category, index) => (
            <CategoryCard
              key={index}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              viewport={{ once: true }}
              onClick={() => handleCategoryClick(category.category)}
            >
              <span className="icon">{category.icon}</span>
              <h3>{category.title}</h3>
              <p>{category.description}</p>
            </CategoryCard>
          ))}
        </CategoriesGrid>
      </CategoriesContent>
    </CategoriesContainer>
  );
};

export default Categories;
