import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { X, ExternalLink, Github, Linkedin, Twitter, Mail, Phone, MapPin, ChevronLeft, ChevronRight } from 'lucide-react';

// Color Palette and Theme
const portfolioTheme = {
  colors: {
    primary: '#2C3E50',      // Deep blue-gray (neutral)
    secondary: '#ECF0F1',    // Light gray (neutral)
    accent: '#E74C3C',       // Vibrant red (accent)
    white: '#FFFFFF',
    text: '#2C3E50',
    textLight: '#7F8C8D',
    overlay: 'rgba(44, 62, 80, 0.8)',
    cardShadow: '0 10px 30px rgba(0, 0, 0, 0.1)',
    hoverShadow: '0 20px 40px rgba(0, 0, 0, 0.15)'
  },
  fonts: {
    heading: "'Poppins', sans-serif",
    body: "'Crimson Text', serif"
  },
  breakpoints: {
    mobile: '768px',
    tablet: '1024px'
  }
};

// Main Portfolio Container
const PortfolioContainer = styled.div`
  min-height: 100vh;
  font-family: ${portfolioTheme.fonts.body};
  color: ${portfolioTheme.colors.text};
  overflow-x: hidden;
`;

// Hero Section
const HeroSection = styled.section`
  height: 100vh;
  background: linear-gradient(${portfolioTheme.colors.overlay}, ${portfolioTheme.colors.overlay}),
              url('https://images.unsplash.com/photo-1498050108023-c5249f4df085?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2072&q=80');
  background-size: cover;
  background-position: center;
  background-attachment: fixed;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  position: relative;
`;

const HeroContent = styled(motion.div)`
  max-width: 800px;
  padding: 0 20px;
  color: ${portfolioTheme.colors.white};
`;

const HeroTitle = styled.h1`
  font-family: ${portfolioTheme.fonts.heading};
  font-size: 4rem;
  font-weight: 700;
  margin-bottom: 1rem;
  letter-spacing: -0.02em;

  @media (max-width: ${portfolioTheme.breakpoints.tablet}) {
    font-size: 3rem;
  }

  @media (max-width: ${portfolioTheme.breakpoints.mobile}) {
    font-size: 2.5rem;
  }
`;

const HeroSubtitle = styled.p`
  font-size: 1.5rem;
  margin-bottom: 2rem;
  opacity: 0.9;
  line-height: 1.6;

  @media (max-width: ${portfolioTheme.breakpoints.mobile}) {
    font-size: 1.2rem;
  }
`;

const HeroButton = styled(motion.button)`
  background: ${portfolioTheme.colors.accent};
  color: ${portfolioTheme.colors.white};
  border: none;
  padding: 15px 40px;
  font-size: 1.1rem;
  font-weight: 600;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: #C0392B;
    transform: translateY(-2px);
  }
`;

// Projects Section
const ProjectsSection = styled.section`
  padding: 100px 20px;
  background: ${portfolioTheme.colors.white};
  min-height: 100vh;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const SectionTitle = styled.h2`
  font-family: ${portfolioTheme.fonts.heading};
  font-size: 3rem;
  text-align: center;
  margin-bottom: 60px;
  color: ${portfolioTheme.colors.primary};

  @media (max-width: ${portfolioTheme.breakpoints.mobile}) {
    font-size: 2.5rem;
  }
`;

const ProjectsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 30px;

  @media (max-width: ${portfolioTheme.breakpoints.tablet}) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (max-width: ${portfolioTheme.breakpoints.mobile}) {
    grid-template-columns: 1fr;
  }
`;

const ProjectCard = styled(motion.div)`
  background: ${portfolioTheme.colors.white};
  border-radius: 15px;
  overflow: hidden;
  box-shadow: ${portfolioTheme.colors.cardShadow};
  transition: all 0.3s ease;
  cursor: pointer;

  &:hover {
    transform: translateY(-10px);
    box-shadow: ${portfolioTheme.colors.hoverShadow};
  }
`;

const ProjectImage = styled.div`
  height: 250px;
  background: url(${props => props.src});
  background-size: cover;
  background-position: center;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: ${portfolioTheme.colors.overlay};
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  ${ProjectCard}:hover &::before {
    opacity: 1;
  }
`;

const ProjectOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  opacity: 0;
  transition: opacity 0.3s ease;

  ${ProjectCard}:hover & {
    opacity: 1;
  }
`;

const ViewMoreButton = styled.button`
  background: ${portfolioTheme.colors.accent};
  color: ${portfolioTheme.colors.white};
  border: none;
