import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import styled from 'styled-components';
import { Search, Filter, Grid, List, MapPin, Star } from 'lucide-react';
import VendorCard from '../../components/VendorCard/VendorCard';
import CityFilter from '../../components/Filters/CityFilter';
import CategoryFilter from '../../components/Filters/CategoryFilter';
import AdvancedFilters from '../../components/Filters/AdvancedFilters';
import SortingOptions from '../../components/Filters/SortingOptions';
import LoadingSpinner from '../../components/UI/LoadingSpinner';

const VendorListContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const PageHeader = styled.div`
  margin-bottom: 2rem;
`;

const PageTitle = styled.h1`
  font-family: ${props => props.theme.fonts.heading};
  font-size: 2.5rem;
  color: ${props => props.theme.colors.primary};
  margin-bottom: 0.5rem;
`;

const ResultsCount = styled.p`
  color: ${props => props.theme.colors.gray};
  font-size: 1.1rem;
  margin-bottom: 1rem;
`;

const ControlsBar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;
  flex-wrap: wrap;
  gap: 1rem;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    flex-direction: column;
    align-items: stretch;
  }
`;

const SearchSection = styled.div`
  display: flex;
  gap: 1rem;
  flex: 1;
  max-width: 500px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    max-width: 100%;
  }
`;

const SearchInput = styled.input`
  flex: 1;
  padding: 0.75rem 1rem 0.75rem 2.5rem;
  border: 2px solid ${props => props.theme.colors.lightGray};
  border-radius: 25px;
  font-size: 1rem;
  background: white;
  position: relative;

  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
  }
`;

const SearchIcon = styled.div`
  position: absolute;
  left: 0.75rem;
  top: 50%;
  transform: translateY(-50%);
  color: ${props => props.theme.colors.gray};
  pointer-events: none;
`;

const ViewControls = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const ViewButton = styled.button`
  background: ${props => props.active ? props.theme.colors.primary : 'white'};
  color: ${props => props.active ? 'white' : props.theme.colors.gray};
  border: 2px solid ${props => props.theme.colors.lightGray};
  padding: 0.5rem;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: ${props => props.theme.colors.primary};
    color: white;
  }
`;

const FilterButton = styled.button`
  background: white;
  color: ${props => props.theme.colors.primary};
  border: 2px solid ${props => props.theme.colors.primary};
  padding: 0.75rem 1.5rem;
  border-radius: 25px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: 500;
  transition: all 0.3s ease;

  &:hover {
    background: ${props => props.theme.colors.primary};
    color: white;
  }
`;

const MainContent = styled.div`
  display: grid;
  grid-template-columns: 300px 1fr;
  gap: 2rem;

  @media (max-width: ${props => props.theme.breakpoints.tablet}) {
    grid-template-columns: 1fr;
  }
`;

const FiltersSection = styled.div`
  @media (max-width: ${props => props.theme.breakpoints.tablet}) {
    display: ${props => props.showFilters ? 'block' : 'none'};
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: white;
    z-index: 1000;
    padding: 2rem;
    overflow-y: auto;
  }
`;

const VendorsSection = styled.div`
  min-height: 400px;
`;

const VendorGrid = styled.div`
  display: grid;
  grid-template-columns: ${props => props.viewMode === 'grid' ? 'repeat(auto-fill, minmax(350px, 1fr))' : '1fr'};
  gap: 2rem;
`;

const LoadingContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 4rem;
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 4rem 2rem;
  color: ${props => props.theme.colors.gray};

  h3 {
    font-size: 1.5rem;
    margin-bottom: 1rem;
  }
`;

const VendorList = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const [vendors, setVendors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [viewMode, setViewMode] = useState('grid');
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    search: searchParams.get('search') || '',
    category: searchParams.get('category') || '',
    city: searchParams.get('city') || '',
    priceRange: [0, 500000],
    rating: 0,
    capacity: '',
    amenities: [],
    specialties: [],
    sortBy: 'popular'
  });

  // Mock vendor data
  useEffect(() => {
    const mockVendors = [
      {
        id: 1,
        name: 'Royal Palace Banquet',
        category: 'Venue',
        type: 'Banquet Hall',
        location: 'Andheri, Mumbai',
        city: 'Mumbai',
        rating: 4.8,
        reviews: 124,
        startingPrice: 250000,
        capacity: '250-700 pax',
        rooms: 4,
        image: '/api/placeholder/400/300',
        badges: ['handpicked', 'verified'],
