const Shopping = () => {
  const { category } = useParams();
  const [searchParams, setSearchParams] = useSearchParams();
  const [activeCategory, setActiveCategory] = useState(category || 'bridal-wear');
  const [searchQuery, setSearchQuery] = useState('');
  const [viewMode, setViewMode] = useState('grid');
  const [showFilters, setShowFilters] = useState(false);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [likedProducts, setLikedProducts] = useState(new Set());
  const [cartItems, setCartItems] = useState(new Set());
  const [filters, setFilters] = useState({
    priceRange: { min: 0, max: 300000 },
    colors: [],
    materials: [],
    sizes: [],
    brands: []
  });

  const categories = [
    { id: 'bridal-wear', name: 'Bridal Wear', icon: 'ðŸ‘°' },
    { id: 'groom-wear', name: 'Groom Wear', icon: 'ðŸ¤µ' },
    { id: 'jewelry', name: 'Jewelry', icon: 'ðŸ’' }
  ];

  const filterOptions = {
    colors: ['red', 'gold', 'pink', 'blue', 'maroon', 'cream', 'silver'],
    materials: ['silk', 'velvet', 'cotton', 'gold', 'diamond', 'silver'],
    sizes: ['S', 'M', 'L', 'XL', 'XXL', 'Free Size'],
    brands: ['Sabyasachi', 'Manish Malhotra', 'Anita Dongre', 'Tarun Tahiliani', 'Rohit Bal', 'Fabindia', 'Tanishq', 'Kalyan Jewellers', 'PC Jeweller']
  };

  useEffect(() => {
    const products = sampleProducts[activeCategory] || [];
    let filtered = products;

    // Search filter
    if (searchQuery) {
      filtered = filtered.filter(product => 
        product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        product.brand.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Price filter
    filtered = filtered.filter(product => 
      product.price >= filters.priceRange.min && 
      product.price <= filters.priceRange.max
    );

    // Other filters
    if (filters.colors.length > 0) {
      filtered = filtered.filter(product => filters.colors.includes(product.color));
    }
    if (filters.materials.length > 0) {
      filtered = filtered.filter(product => filters.materials.includes(product.material));
    }
    if (filters.brands.length > 0) {
      filtered = filtered.filter(product => filters.brands.includes(product.brand));
    }

    setFilteredProducts(filtered);
  }, [activeCategory, searchQuery, filters]);

  const handleCategoryChange = (categoryId) => {
    setActiveCategory(categoryId);
    setSearchParams({ category: categoryId });
  };

  const handleLike = (productId) => {
    const newLiked = new Set(likedProducts);
    if (newLiked.has(productId)) {
      newLiked.delete(productId);
    } else {
      newLiked.add(productId);
    }
    setLikedProducts(newLiked);
  };

  const handleAddToCart = (productId) => {
    const newCart = new Set(cartItems);
    if (newCart.has(productId)) {
      newCart.delete(productId);
    } else {
      newCart.add(productId);
    }
    setCartItems(newCart);
  };

  const handleShare = async (product) => {
    const shareData = {
      title: product.name,
      text: `Check out this ${product.name} by ${product.brand}`,
      url: window.location.href
    };

    if (navigator.share) {
      try {
        await navigator.share(shareData);
      } catch (err) {
        console.log('Error sharing:', err);
      }
    } else {
      // Fallback to clipboard
      navigator.clipboard.writeText(`${shareData.title} - ${shareData.url}`);
      alert('Link copied to clipboard!');
    }
  };

  const toggleFilter = (type, value) => {
    setFilters(prev => ({
      ...prev,
      [type]: prev[type].includes(value)
        ? prev[type].filter(item => item !== value)
        : [...prev[type], value]
    }));
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star
        key={i}
        size={16}
        fill={i < Math.floor(rating) ? '#f39c12' : 'none'}
        color={i < Math.floor(rating) ? '#f39c12' : '#ddd'}
      />
    ));
  };

  return (
    <ShoppingContainer>
      <Header>
        <Title>Wedding Shopping</Title>
        <Subtitle>Find the perfect outfits and accessories for your special day</Subtitle>

        <CategoryTabs>
          {categories.map(cat => (
            <CategoryTab
              key={cat.id}
              active={activeCategory === cat.id}
              onClick={() => handleCategoryChange(cat.id)}
            >
              {cat.icon} {cat.name}
            </CategoryTab>
          ))}
        </CategoryTabs>
      </Header>

      <FilterSection>
        <SearchBar>
          <SearchIcon />
          <SearchInput
            type="text"
            placeholder="Search products..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </SearchBar>

        <FilterControls>
          <FilterButton onClick={() => setShowFilters(true)}>
            <Filter size={18} />
            Filters
          </FilterButton>

          <ViewToggle>
            <ViewButton 
              active={viewMode === 'grid'} 
              onClick={() => setViewMode('grid')}
            >
              <Grid size={18} />
              Grid
            </ViewButton>
            <ViewButton 
              active={viewMode === 'list'} 
              onClick={() => setViewMode('list')}
            >
              <List size={18} />
              List
            </ViewButton>
          </ViewToggle>
        </FilterControls>
      </FilterSection>

      <ProductGrid view={viewMode}>
        {filteredProducts.map(product => (
          <ProductCard
            key={product.id}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
            view={viewMode}
          >
            <ProductImage image={product.image} view={viewMode}>
              <ProductActions>
                <ActionButton onClick={() => handleLike(product.id)}>
                  <Heart 
                    size={20} 
                    fill={likedProducts.has(product.id) ? '#e74c3c' : 'none'}
                    color={likedProducts.has(product.id) ? '#e74c3c' : '#333'}
                  />
                </ActionButton>
                <ActionButton onClick={() => handleShare(product)}>
                  <Share2 size={20} />
                </ActionButton>
                <ActionButton>
                  <Eye size={20} />
                </ActionButton>
              </ProductActions>
            </ProductImage>

            <ProductInfo view={viewMode}>
              <ProductDetails>
                <ProductName>{product.name}</ProductName>
                <ProductBrand>{product.brand}</ProductBrand>

                <ProductPrice>
                  <CurrentPrice>â‚¹{product.price.toLocaleString()}</CurrentPrice>
                  {product.originalPrice && (
                    <OriginalPrice>â‚¹{product.originalPrice.toLocaleString()}</OriginalPrice>
                  )}
                  {product.discount && (
                    <Discount>{product.discount}% OFF</Discount>
                  )}
                </ProductPrice>

                <Rating>
                  <Stars>{renderStars(product.rating)}</Stars>
                  <RatingText>({product.reviews} reviews)</RatingText>
                </Rating>

                <ProductFeatures>
                  {product.features.map((feature, index) => (
                    <Feature key={index}>
                      {feature === 'Free Shipping' && <Truck size={14} />}
                      {feature === 'Easy Returns' && <RotateCcw size={14} />}
                      {feature === 'Authenticity Guaranteed' && <Shield size={14} />}
                      {feature}
                    </Feature>
                  ))}
                </ProductFeatures>
              </ProductDetails>

              <AddToCartButton onClick={() => handleAddToCart(product.id)}>
                <ShoppingCart size={18} style={{ marginRight: '0.5rem' }} />
                {cartItems.has(product.id) ? 'Added to Cart' : 'Add to Cart'}
              </AddToCartButton>
            </ProductInfo>
          </ProductCard>
        ))}
      </ProductGrid>

      <AnimatePresence>
        {showFilters && (
          <FilterModal
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowFilters(false)}
          >
            <FilterContent onClick={(e) => e.stopPropagation()}>
              <FilterHeader>
                <FilterTitle>Filters</FilterTitle>
                <CloseButton onClick={() => setShowFilters(false)}>
                  <X size={24} />
                </CloseButton>
              </FilterHeader>

              <FilterGroup>
                <FilterGroupTitle>Price Range</FilterGroupTitle>
                <PriceRange>
                  <PriceInputs>
                    <PriceInput
                      type="number"
                      placeholder="Min"
                      value={filters.priceRange.min}
                      onChange={(e) => setFilters(prev => ({
                        ...prev,
                        priceRange: { ...prev.priceRange, min: parseInt(e.target.value) || 0 }
                      }))}
                    />
                    <span>to</span>
                    <PriceInput
                      type="number"
                      placeholder="Max"
                      value={filters.priceRange.max}
                      onChange={(e) => setFilters(prev => ({
                        ...prev,
                        priceRange: { ...prev.priceRange, max: parseInt(e.target.value) || 300000 }
                      }))}
                    />
                  </PriceInputs>
                </PriceRange>
              </FilterGroup>

              <FilterGroup>
                <FilterGroupTitle>Colors</FilterGroupTitle>
                <FilterOptions>
                  {filterOptions.colors.map(color => (
                    <FilterOption
                      key={color}
                      active={filters.colors.includes(color)}
                      onClick={() => toggleFilter('colors', color)}
                    >
                      {color}
                    </FilterOption>
                  ))}
                </FilterOptions>
              </FilterGroup>

              <FilterGroup>
                <FilterGroupTitle>Materials</FilterGroupTitle>
                <FilterOptions>
                  {filterOptions.materials.map(material => (
                    <FilterOption
                      key={material}
                      active={filters.materials.includes(material)}
                      onClick={() => toggleFilter('materials', material)}
                    >
                      {material}
                    </FilterOption>
                  ))}
                </FilterOptions>
              </FilterGroup>

              <FilterGroup>
                <FilterGroupTitle>Brands</FilterGroupTitle>
                <FilterOptions>
                  {filterOptions.brands.map(brand => (
                    <FilterOption
                      key={brand}
                      active={filters.brands.includes(brand)}
                      onClick={() => toggleFilter('brands', brand)}
                    >
                      {brand}
                    </FilterOption>
                  ))}
                </FilterOptions>
              </FilterGroup>

              <ApplyFiltersButton onClick={() => setShowFilters(false)}>
                Apply Filters
              </ApplyFiltersButton>
            </FilterContent>
          </FilterModal>
        )}
      </AnimatePresence>
    </ShoppingContainer>
  );
};

